(function($){$('.tab_content--link').click(function(){let id=$(this).attr('data-index');$('.tab_content--box').removeClass('active');$('.tab_content--link').removeClass('active');$('.tab_content').find(`[data-index='${id}']`).toggleClass('active');});$('.tab_content--box .close').click(function(){$('.tab_content--box').removeClass('active');$('.tab_content--link').removeClass('active');});$('.tab-link').click(function(){$('.tab_content--box').removeClass('active');$('.tab_content--link').removeClass('active');});})(jQuery);