(function($){if(window.innerWidth>=576){$(".container__content").slick({dots:false,infinite:true,speed:300,slidesToShow:3,slidesToScroll:3,prevArrow:$(".prev__left-icon"),nextArrow:$(".next__right-icon"),responsive:[{breakpoint:991,settings:{slidesToShow:1,slidesToScroll:3,centerMode:true,},},],});};$('.content__box-icon').click(function(e){e.preventDefault();const title=$(this).data('title');const start_date=$(this).data('start-date');const end_date=$(this).data('end-date');const description=$(this).data('description');const location=$(this).data('location');var calendar_url="http://www.google.com/calendar/event?action=TEMPLATE&text="+title+"&dates="+start_date+"/"+end_date+"&details="+description+"&location="+location+"&ctz=Asia/Ho_Chi_Minh";window.open(calendar_url,'_blank');})})(jQuery);