jQuery(document).ready(function($){$('.sidebar__custom-header').click(function(){$(this).parent().find('.sidebar__menu').slideToggle();$(this).toggleClass('active');});$('.sidebar__menu').each(function(index){if(this){$(this).parent().find('.sidebar__custom-header').addClass('show');}});function adjustDescHeight(){const $desc=$('.desc.custom');let textSeemore='Xem thêm';let textCollapse='Rút gọn';if(fulbright.lang!=='vi'){textSeemore='See more';textCollapse='Collapse';}
let maxHeight=$(window).width()>991?364:398;if($desc.prop('scrollHeight')<=maxHeight){$desc.css('height','fit-content').addClass('hide');$desc.parent().find('.j_btn-explore').css('display','none');}else{$desc.css('height',maxHeight);}
$('.office-president .j_btn-explore').click(function(){$(this).toggleClass('active');$desc.toggleClass('expanded');const heightDesc=$desc.prop('scrollHeight');if($desc.hasClass('expanded')){$desc.css('height',heightDesc+'px');$('.office-president .j_btn-explore').text(textCollapse);}else{$desc.css('height',maxHeight+'px');$('.office-president .j_btn-explore').text(textSeemore);}});}
adjustDescHeight();function redirect(url){window.location.href=url;}
$('.sidebar__sections--wrap .sidebar__sections--item').each(function(){if($(this).find('ul li').length>0){$(this).addClass('show');$(this).find('a').not(':first').click(function(e){redirect($(this).attr('href'));});$(this).children('a').first().click(function(e){e.preventDefault();$(this).parent().toggleClass('active');$(this).parent().find('ul').slideToggle();});}else{let url=$(this).children('a').first().attr('href');$(this).children('a').first().click(function(e){redirect(url);});}});});