jQuery(document).ready(function($){let textSeemore="See more";let textCollapse="Collapse";if(fulbright.lang==="vi"){textSeemore="Xem thêm";textCollapse="Rút gọn";}
const maxHeightListLimit=$(window).width()>991?550:600;$(window).on("load",function(){$(".president__detail-list").each(function(){const detailList=$(this);const scrollHeight=detailList.prop("scrollHeight");console.log("scrollHeight",scrollHeight);console.log("maxHeightListLimit",maxHeightListLimit);console.log(scrollHeight<=maxHeightListLimit);if(scrollHeight<=maxHeightListLimit){detailList.addClass("hiddenMore");}else{detailList.css("height",maxHeightListLimit+"px");detailList.parent().find(".j_btn-more").text(textSeemore);}});});$(document).on("click",".j_btn-more",function(){const detailList=$(this).prev(".president__detail-list");const scrollHeight=detailList.prop("scrollHeight");$(this).toggleClass("active");detailList.toggleClass("expanded");if(detailList.hasClass("expanded")){detailList.css("height",scrollHeight+"px");detailList.removeClass("hiddenMore");$(this).text(textCollapse);}else{detailList.css("height",maxHeightListLimit+"px");$(this).text(textSeemore);}});});