jQuery(document).ready(function($){$(".sidebar .scroll-mb").scroll(function(){var sidebarSectionsHeight=$(".sidebar__sections").height();var scrollHeight=$(".sidebar .scroll-mb").scrollTop();if(scrollHeight+sidebarSectionsHeight>=$(".sidebar__sections")[0].scrollHeight){$(".sidebar .sidebar__sections--wrap").addClass("scrolled-hidden");}else{$(".sidebar .sidebar__sections--wrap").removeClass("scrolled-hidden");}});function redirect(url){window.location.href=url;}
$('.sidebar__sections--wrap .sidebar__sections--item').each(function(){if($(this).find('ul li').length>0){$(this).addClass('show');$(this).find('a').not(':first').click(function(e){redirect($(this).attr('href'));});$(this).children('a').first().click(function(e){e.preventDefault();$(this).parent().toggleClass('active');$(this).parent().find('ul').slideToggle();});}else{let url=$(this).children('a').first().attr('href');$(this).children('a').first().click(function(e){redirect(url);});}});$(window).on('scroll',function(event){event.stopImmediatePropagation();});var url=window.location.href;var value=url.substring(url.lastIndexOf('&')+1);$('.sidebar__sections--item').each(function(){var itemValue=$(this).find('a').attr('href').split('#')[1];if(itemValue===value){$(this).find('a').addClass('active');}});$($('.section-content p b, .section-content p strong')).parents('p').css("margin-top","36px");});