(function($){$(".dropdown-slider__header").click(function(){$(".dropdown-slider__content").toggleClass("active");$(".dropdown-slider__header__icon").toggleClass("active");});$(".explore-more-item ").click(function(event){$(".explore-more-item ").removeClass("active");$(this).addClass("active");});$(".dropdown-slider__header").click(function(){$(this).next(".dropdown-slider__content").slideToggle();});$(".j_btn-explore").click(function(){let contentElement=$(this).parent().find(".description-wrapper");contentElement.toggleClass("active");$(this).toggleClass("active");if(contentElement.hasClass("active")){contentElement.css("height","100%");$(this).text("Collapse");}else{contentElement.height(340);$(this).text("Read More");}});})(jQuery);